import React, { useState } from "react";

export default function SearchBar({onSearch}) {
  const [city, setCity] = useState(''); 
  return (
    <form onSubmit={(e) => {
     e.preventDefault();//importan linea evita se borre lo resivio al renderisas toda la pagina
      onSearch(city);
      setCity('');//le agrego  string vacio para que regresa a estado vacio

    }}>
      <input
        type="search"//es mejor que tipo text
        placeholder="Ciudad..."
       // onSearch={onSearch}
       value ={city}
       onChange={e =>setCity(e.target.value)}
      />
      <input type="submit" value="Agregar"/>
    </form>
  );
}
